import React, { useState, useEffect } from "react";
import io from "socket.io-client";

// Connect to the backend. Make sure the URL/port is correct.
const socket = io("http://localhost:4000");

// Hardcoded users for simple validation
const validUsers = [
  { username: "alice", password: "123" },
  { username: "bob", password: "123" },
  { username: "charlie", password: "123" }
];

function App() {
  // Authentication states
  const [loggedIn, setLoggedIn] = useState(false);
  const [loginError, setLoginError] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  // Chat states
  const [selectedUser, setSelectedUser] = useState("");
  const [message, setMessage] = useState("");
  const [chatLog, setChatLog] = useState([]);

  // This list contains all users except the logged in user
  const availableUsers = validUsers
    .filter((u) => u.username !== username)
    .map(u => u.username);

  // Handle incoming private messages
  useEffect(() => {
    socket.on("private_message", (data) => {
      // data contains sender, receiver and message properties.
      // We update the chat log to display incoming messages.
      setChatLog((prevLog) => [
        ...prevLog,
        `${data.sender} to you: ${data.message}`
      ]);
    });

    socket.on("error_message", (data) => {
      setChatLog((prevLog) => [
        ...prevLog,
        `Error: ${data.error}`
      ]);
    });

    // Cleanup on unmount
    return () => {
      socket.off("private_message");
      socket.off("error_message");
    };
  }, []);

  // Handler for login button
  const handleLogin = (e) => {
    e.preventDefault();
    const user = validUsers.find(
      (u) => u.username === username && u.password === password
    );
    if (user) {
      setLoggedIn(true);
      setLoginError("");
      // Register on the server so that the server knows the socket for this username
      socket.emit("register", username);
    } else {
      setLoginError("Invalid username or password");
    }
  };

  // Handler for sending a private message
  const sendPrivateMessage = () => {
    if (!selectedUser) {
      alert("Select a user to message");
      return;
    }
    if (message.trim() === "") return;

    const data = {
      sender: username,
      receiver: selectedUser,
      message: message.trim()
    };

    // Emit to server for delivering private message
    socket.emit("private_message", data);
    // Optionally, show the sender’s own message locally
    setChatLog((prev) => [
      ...prev,
      `You to ${selectedUser}: ${data.message}`
    ]);
    setMessage("");
  };

  // Render the login form if not logged in
  if (!loggedIn) {
    return (
      <div style={{ padding: "2rem" }}>
        <h2>Login</h2>
        <form onSubmit={handleLogin}>
          <div>
            <label>
              Username:{" "}
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </label>
          </div>
          <div>
            <label>
              Password:{" "}
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </label>
          </div>
          {loginError && <p style={{ color: "red" }}>{loginError}</p>}
          <button type="submit">Login</button>
        </form>
      </div>
    );
  }

  // Render the chat UI if logged in
  return (
    <div style={{ padding: "2rem" }}>
      <h2>Welcome, {username}!</h2>
      <div style={{ marginBottom: "1rem" }}>
        <h3>Select a user to chat with:</h3>
        {availableUsers.map((user) => (
          <button
            key={user}
            onClick={() => setSelectedUser(user)}
            style={{
              marginRight: "0.5rem",
              backgroundColor: selectedUser === user ? "lightblue" : "white"
            }}
          >
            {user}
          </button>
        ))}
      </div>
      {selectedUser ? (
        <div>
          <h3>Chat with {selectedUser}</h3>
          <div
            style={{
              border: "1px solid #ccc",
              height: "300px",
              overflowY: "scroll",
              padding: "1rem",
              marginBottom: "1rem"
            }}
          >
            {chatLog.map((msg, index) => (
              <p key={index}>{msg}</p>
            ))}
          </div>
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && sendPrivateMessage()}
            placeholder="Type a message"
            style={{ width: "80%" }}
          />
          <button onClick={sendPrivateMessage}>Send</button>
        </div>
      ) : (
        <p>Please select a user to start chatting.</p>
      )}
    </div>
  );
}

export default App;
